import React, { useState, useEffect, useRef, useCallback } from 'react';
import Message from './Message';
import { Send, Bot, AlertCircle } from 'lucide-react';
import { conversationService, chatService, ollamaService } from '../services/api';
import { useNotifications } from './NotificationBell';
import analytics from '../services/analytics';

const ChatWindow = ({ conversationId, onNewConversation, onMessagesUpdate }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessages, setLoadingMessages] = useState(false);
  const [error, setError] = useState(null);
  const [useOllama, setUseOllama] = useState(false);
  const [ollamaStatus, setOllamaStatus] = useState(false);
  const [availableModels, setAvailableModels] = useState([]);
  const [selectedModel, setSelectedModel] = useState('xeyalcemilli9032/ostwindgroupai-modern');
  const messagesEndRef = useRef(null);
  const { showNotification } = useNotifications();

  useEffect(() => {
    loadMessages();
    // Track chat start
    analytics.trackChatStart();
  }, [conversationId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Ollama durumunu kontrol et
  useEffect(() => {
    const checkOllamaStatus = async () => {
      try {
        const isRunning = await ollamaService.checkStatus();
        setOllamaStatus(isRunning);

        if (isRunning) {
          const models = await ollamaService.listModels();
          setAvailableModels(models);
          if (models.length > 0) {
            setSelectedModel(models[0].name);
          }
        }
      } catch (error) {
        console.error('Ollama durumu kontrol edilemedi:', error);
        setOllamaStatus(false);
      }
    };

    checkOllamaStatus();
    // Her 30 saniyede bir kontrol et
    const interval = setInterval(checkOllamaStatus, 30000);

    return () => clearInterval(interval);
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadMessages = useCallback(async () => {
    if (!conversationId) return;
    setLoadingMessages(true);
    setError(null);
    try {
      const data = await conversationService.getMessages(conversationId);
      setMessages(data);
      onMessagesUpdate && onMessagesUpdate(data);
    } catch (error) {
      console.error('Mesajlar y√ºklenirken hata:', error);
      setError('Mesajlar y√ºklenemedi');
    } finally {
      setLoadingMessages(false);
    }
  }, [conversationId, onMessagesUpdate]);

  const handleSendMessage = useCallback(async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now().toString(),
      conversation_id: conversationId || 'new',
      role: 'user',
      content: inputMessage.trim(),
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      console.log('üéØ ChatWindow - Sending message:', { conversationId, message: inputMessage.trim(), useOllama });

      let response;

      if (useOllama && ollamaStatus) {
        // Ollama kullan
        console.log('ü§ñ Using Ollama with model:', selectedModel);

        // Track message sent
        analytics.trackMessageSent(inputMessage.trim(), selectedModel);
        const startTime = Date.now();

        response = await ollamaService.sendMessage(inputMessage.trim(), selectedModel);

        // Track response received
        const responseTime = Date.now() - startTime;
        analytics.trackMessageReceived(responseTime, selectedModel);

        const assistantMessage = {
          id: (Date.now() + 1).toString(),
          conversation_id: conversationId || 'ollama-chat',
          role: 'assistant',
          content: response.message,
          timestamp: response.timestamp,
          model: response.model
        };

        setMessages(prev => [...prev, assistantMessage]);
        showNotification('Ollama Yanƒ±tƒ±', {
          message: `${response.model} modeli ile yanƒ±t verildi`,
          type: 'success',
          duration: 3000
        });
      } else {
        // Normal Netlify Functions kullan
        // Track message sent
        analytics.trackMessageSent(inputMessage.trim(), 'groq');
        const startTime = Date.now();

        // Mesaj ge√ßmi≈üini hazƒ±rla (son 10 mesaj)
        const messageHistory = messages.slice(-10).map(msg => ({
          role: msg.role,
          content: msg.content,
          timestamp: msg.timestamp
        }));

        response = await chatService.sendMessage(conversationId || 'new', inputMessage.trim(), messageHistory);

        // Track response received
        const responseTime = Date.now() - startTime;
        analytics.trackMessageReceived(responseTime, 'groq');

        console.log('‚úÖ ChatWindow - Response received:', response);

        const assistantMessage = {
          id: (Date.now() + 1).toString(),
          conversation_id: response.conversation_id,
          role: 'assistant',
          content: response.message,
          timestamp: response.timestamp
        };

        setMessages(prev => [...prev, assistantMessage]);
        showNotification('Yeni AI Yanƒ±tƒ±', {
          message: 'AI asistanƒ±nƒ±z yanƒ±t verdi',
          type: 'success',
          duration: 3000
        });

        if (!conversationId && response.conversation_id) {
          onNewConversation && onNewConversation(response.conversation_id);
        }
      }

      setError(null);
    } catch (error) {
      console.error('‚ùå ChatWindow - Error sending message:', error);
      console.error('‚ùå ChatWindow - Error details:', {
        message: error.message,
        stack: error.stack,
        response: error.response?.data
      });

      // Track error
      analytics.trackError('message_send_error', error.message);

      // Hata durumunda da ger√ßek API'yi √ßaƒüƒ±rmaya √ßalƒ±≈ü
      try {
        console.log('üîÑ Retrying with direct API call...');
        const directResponse = await fetch('/.netlify/functions/chat', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            conversation_id: conversationId || 'new',
            message: inputMessage.trim()
          }),
        });

        if (directResponse.ok) {
          const directData = await directResponse.json();
          console.log('‚úÖ Direct API call successful:', directData);

          const assistantMessage = {
            id: (Date.now() + 1).toString(),
            conversation_id: directData.conversation_id,
            role: 'assistant',
            content: directData.message,
            timestamp: directData.timestamp
          };

          setMessages(prev => [...prev, assistantMessage]);
          showNotification('AI Yanƒ±tƒ±', {
            message: 'AI asistanƒ±nƒ±z yanƒ±t verdi',
            type: 'success',
            duration: 3000
          });
        } else {
          throw new Error(`HTTP ${directResponse.status}`);
        }
      } catch (retryError) {
        console.error('‚ùå Retry also failed:', retryError);

        // Son √ßare olarak demo mesajƒ±
        const demoMessage = {
          id: (Date.now() + 1).toString(),
          conversation_id: conversationId || 'demo',
          role: 'assistant',
          content: `Merhaba! "${inputMessage.trim()}" mesajƒ±nƒ±zƒ± aldƒ±m. Bu bir demo yanƒ±ttƒ±r. OstWindGroup AI asistanƒ±nƒ±z size yardƒ±mcƒ± olmaya hazƒ±r!`,
          timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, demoMessage]);

        showNotification('Demo Yanƒ±t', {
          message: 'Demo yanƒ±t g√∂nderildi',
          type: 'info',
          duration: 3000
        });
      }
    } finally {
      setIsLoading(false);
    }
  }, [inputMessage, isLoading, conversationId, onNewConversation, showNotification]);

  const handleEditMessage = useCallback((messageId, newContent) => {
    setMessages(prev => prev.map(msg => 
      msg.id === messageId ? { ...msg, content: newContent } : msg
    ));
  }, []);

  const formatTime = useCallback((timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('tr-TR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  }, []);

  return (
    <div className="flex flex-col h-full">
      {/* Messages Area */}
      <div className="chat-messages">
        {loadingMessages ? (
          <div className="flex items-center justify-center h-full">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
                <Bot className="w-4 h-4 text-primary" />
              </div>
              <span className="text-muted-foreground">Mesajlar y√ºkleniyor...</span>
            </div>
          </div>
        ) : error ? (
          <div className="empty-state">
            <div className="empty-icon bg-destructive/10">
              <AlertCircle className="w-8 h-8 text-destructive" />
            </div>
            <h3 className="empty-title">Baƒülantƒ± Hatasƒ±</h3>
            <p className="empty-description">{error}</p>
            <button 
              onClick={() => { setError(null); loadMessages(); }} 
              className="modern-button-primary"
            >
              Tekrar Dene
            </button>
          </div>
        ) : messages.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">
              <Bot className="w-12 h-12 text-primary" />
            </div>
            <h2 className="empty-title">
              üöÄ OstWindGroup Modern AI'ya Ho≈ü Geldiniz
            </h2>
            <p className="empty-description">
              D√ºnyanƒ±n en geli≈ümi≈ü AI asistanƒ±! 50+ uzmanlƒ±k alanƒ±nda size yardƒ±mcƒ± olabilirim. A≈üaƒüƒ±ya mesajƒ±nƒ±zƒ± yazƒ±n ve modern sohbetimizi ba≈ülatalƒ±m.
            </p>
            <div className="flex flex-wrap gap-2 justify-center">
              <span className="px-3 py-1 bg-gradient-to-r from-blue-500/10 to-purple-500/10 text-blue-600 dark:text-blue-400 rounded-full text-sm">ü§ñ AI & Makine √ñƒürenmesi</span>
              <span className="px-3 py-1 bg-gradient-to-r from-green-500/10 to-teal-500/10 text-green-600 dark:text-green-400 rounded-full text-sm">üíª Programlama</span>
              <span className="px-3 py-1 bg-gradient-to-r from-purple-500/10 to-pink-500/10 text-purple-600 dark:text-purple-400 rounded-full text-sm">üåê Web Geli≈ütirme</span>
              <span className="px-3 py-1 bg-gradient-to-r from-orange-500/10 to-red-500/10 text-orange-600 dark:text-orange-400 rounded-full text-sm">üé® UI/UX Tasarƒ±m</span>
              <span className="px-3 py-1 bg-gradient-to-r from-indigo-500/10 to-blue-500/10 text-indigo-600 dark:text-indigo-400 rounded-full text-sm">üìä Veri Analizi</span>
              <span className="px-3 py-1 bg-gradient-to-r from-pink-500/10 to-rose-500/10 text-pink-600 dark:text-pink-400 rounded-full text-sm">üöÄ Blockchain & Web3</span>
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            {messages.map((message) => (
              <div key={message.id} className="fade-in">
                <Message
                  message={message}
                  time={formatTime(message.timestamp)}
                  onEdit={handleEditMessage}
                />
              </div>
            ))}
          </div>
        )}

        {isLoading && (
          <div className="flex items-center space-x-3 p-4">
            <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
              <Bot className="w-4 h-4 text-primary" />
            </div>
            <span className="text-sm text-muted-foreground">Yazƒ±yor</span>
            <div className="loading-dots">
              <div className="loading-dot"></div>
              <div className="loading-dot"></div>
              <div className="loading-dot"></div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* AI Control Panel */}
      <div className="ai-control-panel">
        <div className="flex items-center justify-between p-3 bg-gradient-to-r from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-700 rounded-lg border border-blue-200 dark:border-gray-600">
          <div className="flex items-center space-x-3">
            <div className="flex items-center space-x-2">
              <div className={`w-2 h-2 rounded-full ${ollamaStatus ? 'bg-green-500' : 'bg-red-500'}`}></div>
              <span className="text-sm font-medium">Ollama Modern</span>
              <span className="text-xs text-gray-500">({ollamaStatus ? '√áalƒ±≈üƒ±yor' : 'Kapalƒ±'})</span>
            </div>

            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 rounded-full bg-blue-500"></div>
              <span className="text-sm font-medium">Groq API</span>
              <span className="text-xs text-gray-500">(Aktif)</span>
            </div>

            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 rounded-full bg-purple-500"></div>
              <span className="text-sm font-medium">Modern AI</span>
              <span className="text-xs text-gray-500">(50+ Uzmanlƒ±k)</span>
            </div>

            {availableModels.length > 0 && (
              <select
                value={selectedModel}
                onChange={(e) => {
                  const oldModel = selectedModel;
                  const newModel = e.target.value;
                  setSelectedModel(newModel);
                  analytics.trackModelSwitch(oldModel, newModel);
                }}
                className="text-xs px-2 py-1 border rounded bg-white dark:bg-gray-700"
              >
                {availableModels.map((model) => (
                  <option key={model.name} value={model.name}>
                    {model.name.includes('ostwindgroupai-modern') ? 'üöÄ Modern AI' : model.name} ({(model.size / 1024 / 1024 / 1024).toFixed(1)}GB)
                  </option>
                ))}
              </select>
            )}
          </div>

          <div className="flex items-center space-x-2">
            <label className="flex items-center space-x-1 text-xs">
              <input
                type="checkbox"
                checked={useOllama}
                onChange={(e) => setUseOllama(e.target.checked)}
                className="rounded"
                disabled={!ollamaStatus}
              />
              <span>Modern AI Kullan</span>
            </label>
          </div>
        </div>
      </div>

      {/* Input Area */}
      <div className="chat-input-area">
        <form onSubmit={handleSendMessage} className="relative">
          <div className="flex items-end space-x-3">
            <div className="flex-1 relative">
              <textarea
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Mesajƒ±nƒ±zƒ± yazƒ±n..."
                disabled={isLoading}
                className="modern-input"
                rows={1}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage(e);
                  }
                }}
                onInput={(e) => {
                  e.target.style.height = 'auto';
                  e.target.style.height = Math.min(e.target.scrollHeight, 128) + 'px';
                }}
              />
            </div>

            <button
              type="submit"
              disabled={!inputMessage.trim() || isLoading}
              className="modern-button-primary hover-lift disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isLoading ? (
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
              ) : (
                <Send className="w-4 h-4" />
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ChatWindow;
