import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Button } from './ui/Button';
import Message from './Message';
import { Send, Bot } from 'lucide-react';
import { conversationService, chatService } from '../services/api';
import EmojiPickerButton from './EmojiPickerButton';
import { FileUploadButton, FilePreview } from './FileUpload';
import VoiceMessageButton from './VoiceMessageButton';

const ChatWindow = ({ conversationId, onNewConversation, onMessagesUpdate }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessages, setLoadingMessages] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (conversationId) {
      loadMessages();
    } else {
      setMessages([]);
    }
  }, [conversationId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Klavye kÄ±sayollarÄ±
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl/Cmd + Enter ile mesaj gÃ¶nder
      if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        if (inputMessage.trim() && !isLoading) {
          handleSendMessage(e);
        }
      }
      // Escape ile input'u temizle
      if (e.key === 'Escape') {
        setInputMessage('');
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [inputMessage, isLoading]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadMessages = useCallback(async () => {
    if (!conversationId) return;
    
    setLoadingMessages(true);
    try {
      const data = await conversationService.getMessages(conversationId);
      setMessages(data);
    } catch (error) {
      console.error('Mesajlar yÃ¼klenirken hata:', error);
    } finally {
      setLoadingMessages(false);
    }
  }, [conversationId]);

  const handleSendMessage = useCallback(async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now().toString(),
      conversation_id: conversationId,
      role: 'user',
      content: inputMessage.trim(),
      timestamp: new Date().toISOString()
    };

    // Optimistic update
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      console.log('Sending message:', inputMessage.trim(), 'to conversation:', conversationId || 'new');
      const response = await chatService.sendMessage(conversationId || 'new', inputMessage.trim());
      console.log('Received response:', response);
      
      // Update messages with server response
      setMessages(prev => {
        const filtered = prev.filter(msg => msg.id !== userMessage.id);
        return [...filtered, response.user_message, response.assistant_message];
      });

      // If this was a new conversation, trigger conversation list refresh
      if (!conversationId && response.conversation_id) {
        if (onNewConversation) {
          onNewConversation();
        }
      }
    } catch (error) {
      console.error('Mesaj gÃ¶nderilirken hata:', error);
      // Remove the optimistic message on error
      setMessages(prev => prev.filter(msg => msg.id !== userMessage.id));
    } finally {
      setIsLoading(false);
    }
  }, [inputMessage, isLoading, conversationId, onNewConversation]);

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('tr-TR', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleEmojiSelect = (emoji) => {
    setInputMessage(prev => prev + emoji);
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
  };

  const handleFileRemove = () => {
    setSelectedFile(null);
  };

  const handleEditMessage = (messageId, newContent) => {
    setMessages(prev => prev.map(msg => 
      msg.id === messageId ? { ...msg, content: newContent } : msg
    ));
    // TÃ¼m mesajlarÄ± gÃ¼ncelle
    if (onMessagesUpdate) {
      onMessagesUpdate(prev => prev.map(msg => 
        msg.id === messageId ? { ...msg, content: newContent } : msg
      ));
    }
  };

  const handleVoiceMessage = async (audioBlob, convertedText = null) => {
    try {
      setIsLoading(true);
      
      // Use converted text if available, otherwise use demo message
      const voiceMessage = convertedText || "ðŸŽ¤ Sesli mesaj gÃ¶nderildi (Ses-metin Ã§evirisi aktif deÄŸil)";
      
      if (!conversationId) {
        const newConversation = await conversationService.createConversation();
        if (onNewConversation) {
          onNewConversation();
        }
      }

      const response = await chatService.sendMessage(conversationId || 'new', voiceMessage);
      
      // MesajlarÄ± gÃ¼ncelle
      setMessages(prev => [...prev, response.user_message, response.assistant_message]);
      
      // TÃ¼m mesajlarÄ± gÃ¼ncelle
      if (onMessagesUpdate) {
        onMessagesUpdate(prev => [...prev, response.user_message, response.assistant_message]);
      }
      
    } catch (error) {
      console.error('Sesli mesaj gÃ¶nderilirken hata:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* Header */}
      <div className="p-6 border-b bg-card/50 backdrop-blur-sm">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
              <Bot className="w-4 h-4 text-primary" />
            </div>
            <h2 className="text-lg font-semibold text-foreground">
              {conversationId ? 'AI Asistan' : 'Yeni Sohbet'}
            </h2>
          </div>
          
          {conversationId && (
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-xs text-muted-foreground">Ã‡evrimiÃ§i</span>
            </div>
          )}
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-6 space-y-6 bg-gradient-to-b from-background/50 to-background/30">
        {loadingMessages ? (
          <div className="flex justify-center items-center h-32">
            <div className="text-muted-foreground">Mesajlar yÃ¼kleniyor...</div>
          </div>
        ) : messages.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-center">
            <Bot className="w-16 h-16 text-muted-foreground mb-4" />
            <h3 className="text-xl font-semibold mb-2">OstWindGroup AI'ya HoÅŸ Geldiniz</h3>
            <p className="text-muted-foreground mb-4">
              Size nasÄ±l yardÄ±mcÄ± olabilirim? AÅŸaÄŸÄ±ya mesajÄ±nÄ±zÄ± yazÄ±n.
            </p>
          </div>
        ) : (
          messages.map((message) => (
            <Message
              key={message.id}
              message={message}
              time={formatTime(message.timestamp)}
              onEdit={handleEditMessage}
            />
          ))
        )}
        
        {isLoading && (
          <div className="flex items-center space-x-3 text-muted-foreground">
            <Bot className="w-5 h-5" />
            <div className="flex items-center space-x-2">
              <span className="text-sm">YazÄ±yor</span>
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-current rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-6 border-t bg-card/50 backdrop-blur-sm">
        {selectedFile && (
          <div className="mb-4">
            <FilePreview file={selectedFile} onRemove={handleFileRemove} />
          </div>
        )}
        
        <div className="relative">
          <form onSubmit={handleSendMessage} className="relative">
            <div className="relative bg-background/80 backdrop-blur-sm border border-border/50 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-200 focus-within:ring-2 focus-within:ring-primary/20 focus-within:border-primary/50">
              <textarea
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="MesajÄ±nÄ±zÄ± yazÄ±n... (Ctrl+Enter gÃ¶nder, Esc temizle)"
                disabled={isLoading}
                className="w-full min-h-[52px] max-h-32 px-4 py-3 pr-24 bg-transparent border-0 rounded-2xl resize-none focus:outline-none placeholder:text-muted-foreground/70 text-foreground"
                rows={1}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage(e);
                  }
                }}
                onInput={(e) => {
                  e.target.style.height = 'auto';
                  e.target.style.height = Math.min(e.target.scrollHeight, 128) + 'px';
                }}
              />
              
              {/* Action buttons */}
              <div className="absolute bottom-3 right-3 flex items-center space-x-2">
                <div className="flex items-center space-x-1 bg-muted/50 rounded-full p-1">
                  <VoiceMessageButton onVoiceMessage={handleVoiceMessage} disabled={isLoading} />
                  <FileUploadButton onFileSelect={handleFileSelect} disabled={isLoading} />
                  <EmojiPickerButton onEmojiSelect={handleEmojiSelect} />
                </div>
                
                <Button 
                  type="submit" 
                  disabled={(!inputMessage.trim() && !selectedFile) || isLoading}
                  className="h-8 w-8 rounded-full bg-primary hover:bg-primary/90 shadow-lg hover:shadow-xl transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                  size="icon"
                >
                  {isLoading ? (
                    <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
                  ) : (
                    <Send className="w-4 h-4" />
                  )}
                </Button>
              </div>
            </div>
          </form>
          
          {/* Character count and tips */}
          <div className="flex items-center justify-between mt-2 px-1">
            <div className="flex items-center space-x-4 text-xs text-muted-foreground">
              <span className="flex items-center space-x-1">
                <kbd className="px-1.5 py-0.5 bg-muted rounded text-xs">Ctrl</kbd>
                <span>+</span>
                <kbd className="px-1.5 py-0.5 bg-muted rounded text-xs">Enter</kbd>
                <span>gÃ¶nder</span>
              </span>
              <span className="flex items-center space-x-1">
                <kbd className="px-1.5 py-0.5 bg-muted rounded text-xs">Esc</kbd>
                <span>temizle</span>
              </span>
            </div>
            
            {inputMessage.length > 0 && (
              <div className="text-xs text-muted-foreground">
                {inputMessage.length} karakter
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
